import './App.css';
import './css/main.css'

function App() {

  function hamburgerClick() {
    const btn = document.getElementById('menu-btn')
    const nav = document.getElementById('menu')
    btn.classList.toggle('open')
    nav.classList.toggle('flex')
    nav.classList.toggle('hidden')
  }




  return (
    <div className="App">
      <nav class="relative container mx-auto p-6 ">

{/* <!--flex Container--> */}
<div class="flex items-center justify-between">
    {/* <!--logo--> */}
    <h1 className='text-4xl'>Encara Coding</h1>

    {/* <!--Menu--> */}
    <div class="hidden md:flex space-x-6">

        <a href="#" class="hover:text-darkGrayishBlue">Pricing</a>
        <a href="#" class="hover:text-darkGrayishBlue">Product</a>
        <a href="#" class="hover:text-darkGrayishBlue">About Us</a>
        <a href="#" class="hover:text-darkGrayishBlue">Careers</a>
        <a href="#" class="hover:text-darkGrayishBlue">Comminity</a>
    </div>

    {/* <!--Button--> */}
    <a href="#" class="hidden md:block p-3 px-6 pt-2 text-white bg-brightRed rounded-full baseline hover:bg-brightRedLight">Get Started</a>

    {/* <!--Hamburger Icon--> */}
    <button onClick={hamburgerClick} id="menu-btn" class=" block hamburger md:hidden focus:outine-none">
        <span class="hamburger-top"></span>
        <span class="hamburger-middle"></span>
        <span class="hamburger-bottom"></span>
    </button>
</div>

{/* <!--Mobile menu--> */}
<div class="md:hidden">
    <div id="menu" class="absolute  hidden flex-col items-center self-end  py-8 mt-10 space-y-6 font-bold bg-white sm:w-auto sm:self-center left-6 right-6 drop-shadow-md">
        <a href="#">Pricing</a>
        <a href="#">Product</a>
        <a href="#">About us</a>
        <a href="#">Careers</a>
        <a href="#">Community</a>
    </div>
</div>
</nav>
    </div>
  );
}

export default App;
