import React, { useState } from 'react'

const ContactMe = () => {
    const [name , setName] = useState("") ;
    const [email ,setEmail] = useState("") ;
    const [phone , setPhone] =useState("") ;
    const [subject , setSubject] = useState("") ;

    function contact() {

